%		period=229;
%		opt.trialNum=5;
%		opt.wingRatio=0.1;
%		mark=periodicMarkId(signal, period, opt, 1);

%	Roger Jang, 20120412

if nargin<1, selfdemo; return; end
if nargin<3 | isempty(opt)
	opt.trialNum=5;
	opt.wingRatio=0.1;
end
if nargin<4, plotOpt=0; end

for i=1:opt.trialNum
	mark(i).signal=signal;
	[maxValue, globalMaxIndex]=max(signal);
	mark(i).globalMaxIndex=globalMaxIndex;
	wing=round(period*opt.wingRatio);
	maxIndex=globalMaxIndex;
	beatPos=[maxIndex];
	while 1		% Search forward
		nextCenter=maxIndex+period;
		index1=nextCenter-wing;
		index2=nextCenter+wing;
		if index1>length(signal), break; end
		if index2>length(signal), index2=length(signal); end
		[maxValue, localMaxIndex]=max(signal(index1:index2));
		maxIndex=localMaxIndex+index1-1;
		beatPos=[beatPos, maxIndex];
	end
	maxIndex=globalMaxIndex;
	while 1		% Search backward
		nextCenter=maxIndex-period;
		index1=nextCenter-wing;
		index2=nextCenter+wing;
		if index2<1, break; end
		if index1<1, index1=1; end
		[maxValue, localMaxIndex]=max(signal(index1:index2));
		maxIndex=localMaxIndex+index1-1;
		beatPos=[maxIndex, beatPos];
	end
	mark(i).position=beatPos;
	mark(i).weight=mean(signal(mark(i).position));
	% ===== Zero the value around global maximum
	id1=globalMaxIndex-wing;
	id2=globalMaxIndex+wing;
	if id1<1, id1=1; end
	if id2>length(signal), id2=length(signal); end
	signal(id1:id2)=0;
end

if plotOpt
	for i=1:opt.trialNum
		subplot(opt.trialNum, 1, i);
		plot(mark(i).signal);
		set(gca, 'xtick', []); set(gca, 'ytick', []);	% To make to subplot bigger and evenly distributed!
		set(gca, 'xlim', [-inf inf]);
		line(mark(i).globalMaxIndex, mark(i).signal(mark(i).globalMaxIndex), 'marker', '^', 'color', 'k');
		line(mark(i).position, mark(i).signal(mark(i).position), 'marker', '.', 'color', 'r', 'linestyle', 'none');
	%	title(sprintf('Waveform with computed marks of mean height=%f', mark(i).weight));	% To make the subplot even distributed
	%	ylabel(sprintf('Mean height=%f', mark(i).weight));
	end
end

% ====== Self demo
function selfdemo
mObj=mFileParse(which(mfilename));
strEval(mObj.example);
